public:

	DM::Image ColourImage;

	DMColmap(){ Initialise(1); }

	void Initialise(int choice)
	{
		std::vector<float> R(256);
		std::vector<float> G(256);
		std::vector<float> B(256);

		ColourImage = DM::RGBImage("rgb in class", 4, 256, 1);
		ColourImage.GetOrCreateImageDocument().Show();
		ColourImage.GetOrCreateImageDocument().Clean();
		Gatan::PlugIn::ImageDataLocker rgbLocker(ColourImage);
		int* rgbData = (int*)rgbLocker.get();
		ColourImage.GetOrCreateImageDocument().Close(FALSE);

		getArray(choice, R, G, B);

		for (int i = 0; i<256; i++)
			rgbData[i] = RGB(B[i], G[i], R[i]);

		rgbLocker.~ImageDataLocker();
	}

	void Set(DMImage &Input)
	{
		DM::ImageDisplaySetInputColorTable(Input.Display, ColourImage);
	}

